import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-apirequest',
  templateUrl: './apirequest.component.html',
  styleUrls: ['./apirequest.component.css'],
})
export class ApirequestComponent implements OnInit {
  url = 'https://jsonplaceholder.typicode.com/posts';
  data: any;
  constructor(private http: HttpClient) {}
  result = null;
  ngOnInit(): void {
    // this.http.get(this.url).subscribe((result) => {
    //   console.log(result);
    //   this.data = result;
    // });


    this.http.get(this.url).pipe(map(pos:any)=>{
      return pos.filter((p:any)=>p.i)
    }).subscribe((result) => {
      console.log(result);
      this.data = result;
    });
  }
}
<p>apirequest works!</p>

<div *ngFor="let item of data">
  <h6>{{ item.id }}</h6>
  <h6>{{ item.title }}</h6>
  <h6>{{ item.body }}</h6>
</div>
