<div class="row">
  <ng-container *ngIf="!isloading">
    <div class="col-3 mt-3" *ngFor="let item of data">
      <div class="card" (click)="selectdata(item)">
        <div class="card-body">
          <h5>{{ item.userId }}</h5>
          <h6>{{ item.title }}</h6>
          <h6>{{ item.body }}</h6>
        </div>
      </div>
    </div>
  </ng-container>
</div>
