import { Component } from '@angular/core';

@Component({
  selector: 'app-hello',
  templateUrl: './hello.component.html',
  styleUrls: ['./hello.component.css'],
})
export class HelloComponent {
  nameOfParent: string = 'From Parent Hello Dynamically.';
  email: string = 'NothinG';
  onsubmit() {
    console.log('onsubmit function hitted');
  }
  onchange(ev: any) {
    // this.nameOfParent = ev.target.value();
    console.log(ev.target.value());
  }
  // onchange() {
  //   console.log('onchange function hitted');
  // }
}
<!-- <p>hello works!</p> -->
<!-- <app-home name="jion"></app-home> -->
<app-home name="{{ nameOfParent }}"></app-home>

<input
  (keyup)="onchange($event)"
  placeholder="Enter Name"
  id="name"
  [(ngModel)]="nameOfParent"
/>
<button (click)="onsubmit()">Add</button>
<p>your name is : {{ nameOfParent }}</p>

<input placeholder="Enter Email" id="email" [(ngModel)]="email" />
<p>Phone : {{ email }}</p>
<!-- <app-home name="{{ var2 }}"></app-home> -->
<app-home name="{{ nameOfParent }}" [email]="email"></app-home>
