import { Component } from '@angular/core';

@Component({
  selector: 'employee',
  templateUrl: 'employee.component.html',
})
export class EmployeeComponent {
  constructor() {}

  ngOnInit() {}

  isTrue = 'True';
}
<h1>Hello guys! It's my first Angular.</h1>
<p *ngIf="isTrue">True</p>
<p *ngIf="!isTrue">False</p>
<app-hello *ngIf="!isTrue"></app-hello>
<div *ngFor="let item of items" , index as i>{{ i }} == {{ item }}</div>
